document.addEventListener("click",(e=>{if(e.target.closest(".reviews__btn")){const s=e.target.closest(".reviews__btn").querySelector(".reviews__like");let t=parseInt(s.querySelector("span").innerText);s.classList.contains("like")&&t>0?(t--,s.innerHTML=`<span>${t}</span>`,s.classList.remove("like")):(t++,s.innerHTML=`<span>${t}</span>`,s.classList.add("like"))}}));